mapper.insert:
INSERT INTO IMAGE_T (
    IMAGE_PATH
  , FILESYSTEM_NAME
  , THUMBNAIL
  , PRODUCT_NO
) VALUES (
    #{imagePath}
  , #{filesystemName}
  , #{thumbnail}
  , #{productNo}
)

INSERT INTO IMAGE_T (
    IMAGE_PATH
  , FILESYSTEM_NAME
  , PRODUCT_NO
) VALUES (
    #{imagePath}
  , #{filesystemName}
  , #{productNo}
)

mapper.update:
UPDATE PRODUCT_T
   SET HIT = HIT + 1
 WHERE PRODUCT_NO = #{productNo}

UPDATE PRODUCT_T
   SET TRIP_NAME = #{tripName}
     , TRIP_CONTENTS = #{tripContents}
     , GUIDE = #{guide} 
     , TIMETAKEN = #{timetaken}
     , PRICE = #{price}
     , DANGER = #{danger} 
     , REGISTERED_AT = SYSDATE
     , MODIFIED_DATE = SYSDATE
     , PLAN = #{plan} 
     , STATUS = #{status}
     , TERM_USE = #{termUse}
 WHERE PRODUCT_NO = #{productNo}

mapper.select:
SELECT PRODUCT_NO, IMAGE_PATH, FILESYSTEM_NAME
  FROM IMAGE_T
 WHERE PRODUCT_NO = #{productNo}

mapper.insert:
INSERT INTO REVIEW_T ( REVIEW_NO , PRODUCT_NO
, USER_NO , CONTENTS , CREATED_AT , MODIFIED_AT , STATUS , STAR ) VALUES ( REVIEW_SEQ.NEXTVAL , #{productNo}
, #{userNo} , #{contents} , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH:MI:SS') , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH:MI:SS') , 1 , 0 )

mapper.select:
SELECT COUNT(*)
  FROM REVIEW_T
 WHERE PRODUCT_NO = #{productNo}
