mapper.select:
SELECT USER_NO, EMAIL, PW, NAME, GENDER, MOBILE, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, AGREE, STATE, AUTH, PW_MODIFIED_AT, JOINED_AT
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}
  AND USER_NO = #{userNo}

mapper.insert:
INSERT INTO ACCESS_T (
	EMAIL
  , LOGIN_AT
  ) VALUES (
  	#{email}
  , SYSDATE
  )

	 INSERT INTO USER_T (
        USER_NO
      , EMAIL
      , PW
      , NAME
      , GENDER
      , MOBILE
      , POSTCODE
      , ROAD_ADDRESS
      , JIBUN_ADDRESS
      , DETAIL_ADDRESS
      , AGREE
      , STATE
      , AUTH
      , PW_MODIFIED_AT
      , JOINED_AT
    ) VALUES (
        USER_SEQ.NEXTVAL
      , #{email}
      , #{pw}
      , #{name}
      , #{gender}
      , #{mobile}
      , #{postcode}
      , #{roadAddress}
      , #{jibunAddress}
      , #{detailAddress}
      , #{agree}
      , 0
      , 1
      , SYSDATE
      , SYSDATE
    )

mapper.update:
UPDATE USER_T
   SET NAME = #{name}
     , GENDER = #{gender}
     , MOBILE = #{mobile}
     , POSTCODE = #{postcode}
     , ROAD_ADDRESS = #{roadAddress}
     , JIBUN_ADDRESS = #{jibunAddress}
     , DETAIL_ADDRESS = #{detailAddress}
     , AGREE = #{agree}
 WHERE USER_NO = #{userNo}

UPDATE USER_T
   SET PW = #{pw}
     , PW_MODIFIED_AT = SYSDATE
 WHERE USER_NO = #{userNo}

mapper.insert:
